(142, 'new_appointment_admin_subject', '<Strong>[blog-name]</Strong>: New appointment scheduled by <Strong>[client-name]</Strong>', 'yes'),
(143, 'new_appointment_admin_body', '\r\nHi Admin,\r\n\r\n<h1> Appointment details are: </h1>\r\n<p>\r\nClient Name: <strong>[client-name]</strong> <br/>\r\nClient Email: <strong>[client-email]</strong> <br/>\r\nClient Phone:<strong> [client-phone]</strong> <br/>\r\nClient Special Instruction: <strong>[client-si]</strong> <br/>\r\n<br/><br/>\r\nAppointment For:<strong> [service-name]</strong> <br/>\r\nAppointment Date: <strong>[app-date]   </strong> <br/>\r\nAppointment Time: <strong>[app-time</strong>   <br/>\r\nAppointment Status:<strong> [app-status]</strong> <br/>\r\n\r\n</p>\r\n<h3> View this appointment at admin dashboard. <h3>\r\n<p>\r\n<br/>\r\nBest Regards\r\n[blog-name]\r\n', 'yes'),
(144, 'new_appointment_client_subject', '<Strong>[blog-name]</Strong>: Your appointment status is <Strong>[app-status]</Strong>', 'yes'),
(145, 'new_appointment_client_body', '\r\n<p>\r\nHi [client-name], <br/>\r\n\r\nThank you for scheduling appointment with <Strong>[blog-name]</Strong>. <br/><br/>\r\n\r\nYour appointment for<Strong> [service-name] <Strong>on<Strong> [app-date]<Strong> at <Strong>[app-time].</Strong><br/><br/>\r\n\r\nCurrently, your appointment status is <Strong>[app-status].</Strong> </br><br/>\r\n\r\nYou will get a confirmation mail once admin accepts the appointment. </br>\r\n</p><br/>\r\nBest Regards\r\n<h3> [blog-name] </h3>\r\n', 'yes'),
(146, 'approve_appointment_client_subject', '<Strong>[blog-name]</Strong>: Your appointment status is <Strong>[app-status]</Strong>', 'yes'),
(147, 'approve_appointment_client_body', '\r\nHi <strong>[client-name]</strong>,\r\n<br/>\r\nYour appointment has been <strong>[app-status]</strong> by admin.</br>\r\n<br/>\r\nNow, your appointment for <strong>[service-name]</strong> on <strong>[app-date]</strong> at <strong>[app-time]</strong>.</br>\r\n<br/>\r\nThank you for scheduling appointment with <strong>[blog-name]</strong>.</br>\r\n</br>\r\n<br/>\r\nBest Regards\r\n[blog-name]\r\n', 'yes'),
(148, 'cancel_appointment_client_subject', '<strong>[blog-name]</strong>: Your appointment status is <strong>[app-status]</strong>', 'yes'),
(149, 'cancel_appointment_client_body', '\r\nHi <strong>[client-name]</strong>,<br/><br/>\r\n<br/>\r\n<p>\r\nSorry! Due to some reason we are unable to complete your appointment request.<br/><br/>\r\n\r\nNow, your appointment for <strong>[service-name]</strong> on <strong>[app-date]</strong> at [app-time] has been <strong>[app-status]</strong> by admin.<br/><br/>\r\n\r\nThank you for scheduling appointment with <strong>[blog-name]</strong>.<br/><br/>\r\n</p>\r\n<br/><br/>\r\nBest Regards\r\n<h3> [blog-name] </h3>\r\n', 'yes'),
